sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const table = event.payload.table;
    const encodedQuery = event.payload.query;
    const company = "Yansa Labs";

    const mergePARFiltersV2 = imports["global.mergePARFilters"]().v2;
    api.setState("parFilters", ({ currentValue, api }) => {
        const { parFilters, encodedQueries } = mergePARFiltersV2(
            currentValue,
            [{
                apply_to: [table + ".company_organization"], 
                filterId: event.elementId,
                include_unmatched_or_empty: false,
                label: "Company/Organization",
                order: 9000,
                source: {
                    type: "table",
                    encodedQuery: "",
                    choices: undefined,
                    emptyOption: undefined,
                    payload: {
                        field: undefined,
                        primaryKey: undefined,
                        table: table
                    }
                },
                type: "choice",
                values: [
                    company
                ]
            }],
        );

        api.setState("encodedQueries", encodedQueries);
        console.log("parFilters", parFilters);
        console.log("encodedQueries", encodedQueries);
        return parFilters;
    });

}
