sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  const shortDesc = api.item.value.short_description.displayValue;
  const truncateLength = 40;
  if (shortDesc.length > truncateLength) return shortDesc.slice(0, truncateLength) + "...";
	return shortDesc;
}
